import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import { render } from '@testing-library/react';

class App extends Component{
  constructor(){
    super()
    this.state = {
      monsters: [
        {
          name: 'frankenstein'
        },
        {
          name: 'dracula'
        },
        {
          name: 'zombie'
        }
      ]
    }
  }

  render() {
    return(
      <div className = 'App'>
        {
          this.state.monsters.map(monster => <h1>{monster.name}</h1>)
        }
      </div>
    )
  }
}

export default App;
